import React from 'react';
import Masonry from 'react-masonry-component';
import PagePagination from '../../components/PagePagination';
import { withPostConsumer } from "../../context";

const masonryOptions = {
    transitionDuration: 0
};

const Posts = ({context}) => {
	const {posts} = context;
	console.log(posts);

	let postList = posts.map( item => {
		return (
			<article className="posts__article">
				<a className="posts__thumbnail" href="#" aria-hidden="true">
					<img
						src={item.image}
						alt=""
					/>
				</a>
				<header className="posts__entry-header">
					{ item.featured &&
						<span className="posts__badges">Featured</span>
					}

					<h2 className="posts__entry-title">
						<a href="#" rel="bookmark">{item.title}</a>
					</h2>
					<div className="posts__entry-meta">
						<span className="posts__cat">{item.category}</span>
						<span className="posts__date">August 8, 2019</span>
					</div>
				</header>

				<div className="posts__entry-summary">
					<p>{item.summary}...</p>
				</div>
			</article>
		)

	});

	return (
		<React.Fragment>
		<section className="posts">
			<Masonry className={'posts__layout'} options={masonryOptions}>
				{postList}
			</Masonry>
		</section>
		<PagePagination />
		</React.Fragment>
	);
}


export default withPostConsumer(Posts);
