import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import $ from "jquery";

const StickyNavigation = (props) => {

	const closeMenu = (e) =>  {
		let target = e.target.dataset.target;
		$(`#${target}`).addClass('--hidden');
	}

	return (
		<nav id="nav-sidebar" class="nav-sidebar --hidden">
			<div class="nav-sidebar__container">
				<ul class="nav-sidebar__list">
					<li class="nav-sidebar__item"><a href="#">Home</a></li>
					<li class="nav-sidebar__item"><a href="#">About Us</a></li>
					<li class="nav-sidebar__item"><a href="#">Portfolio</a></li>
					<li class="nav-sidebar__item"><a href="#">Social Life</a></li>
					<li class="nav-sidebar__item"><a href="#">Follow Me</a></li>
				</ul>
			</div>
			<a href="javascript:void(0)" class="nav-sidebar__close" data-target="nav-sidebar" onClick={closeMenu}><FontAwesomeIcon icon={faTimes} /></a>
		</nav>
	);
}


export default StickyNavigation;
