import React, {Component} from 'react';
import Client from './contentful';

const PostContext = React.createContext();

class PostProvider extends Component {
	state = {
		posts: [],
		filterPosts: [],
		loading: true,
		keyword: ''
	}

	getData = async () => {
		try{
			let response = await Client.getEntries({
				content_type: "post",
				order: "fields.dateUpdated"
			});

			let posts = this.formatData(response.items);
			console.log(posts);

			this.setState({
				posts: response.items,
				loading: false
			});

			console.log(response);

		} catch (error) {
			console.log(error);
		}
	}

	componentDidMount () {
		this.getData();
	}

	formatData (items) {
		let tempItems = items.map(item => {

			let id = item.sys.id
			let image = item.fields.image.fields.file.url;
			let author = {
				name: item.fields.author.fields.name,
				description: item.fields.author.fields.description,
				avatar: item.fields.author.fields.avatar.fields.file.url
			}

			let dateCreated = this.formatDate(item.fields.dateCreated);
			let dateUpdated = this.formatDate(item.fields.dateUpdated);

			let post = {...item.fields, id, image, author, dateCreated, dateUpdated};

			return post;
		});

		return tempItems;
	}

	formatDate = (date) => {
		let tempDate = new Date(date);
		let options =  {
			month: 'long', 
			day: 'numeric',
			year: 'numeric'
		};

		tempDate.toLocaleString('en-US', options);
		// let string = tempDate.getFullYear() + '-' + ("0" + (tempDate.getMonth() + 1)).slice(-2) + '-' + ("0" + tempDate.getDate()).slice(-2);
		return tempDate;
	} 

	render() {
		return(
			<PostContext.Provider value={{...this.state}}>
				{this.props.children}
			</PostContext.Provider>
		)
	}
}

const PostConsumer = PostContext.Consumer;

export function withPostConsumer(Component) {
	return function consumerWrapper(props) {
		return <PostConsumer>
			{value => <Component {...props} context={value} /> }
		</PostConsumer>
	}
}

export {PostProvider, PostConsumer, PostContext}
