import React from 'react';
import { withPostConsumer } from "../../context";
import Loading from '../../components/Loading';
import Posts from '../../components/Posts';
import PageHeader from '../../components/PageHeader';

const Search = ({context}) => {
	const {filterPosts, keyword, Loading} = context;
	console.log(keyword);

	if(Loading) {
		return <Loading />;
	}

	return (
		<React.Fragment>
			<PageHeader>
				<h1 className="page-header__title">Search result for {`"${keyword}"`}</h1>
				<h3 className="page-header__sub-title align-left">{filterPosts.length}&nbsp;{filterPosts.lenght > 1 ? "results" : "result"}</h3>
			</PageHeader>
			<Posts dataList={filterPosts} />
		</React.Fragment>
	)
}

export default withPostConsumer(Search)
