import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronRight, faChevronLeft } from "@fortawesome/free-solid-svg-icons"

const PagePagination = (props) => {
	let pages = [];
	console.log(props.totalOfPage);

	for (let i=1; i <= props.totalOfPage; i++) {
		if(i === props.currentPage) {
			pages.push(<span className="page-navigation__item --page-number --current">{i}</span>);
		} else {
			pages.push(<span href="!#" className="page-navigation__item --page-number">{i}</span>)
		}
	}
	// let pages = props.totalOfPage.map(item => {
	// 	if(item === props.currentPage) {
	// 		return <span
	// 					href="!#"
	// 					className="page-navigation__item --page-number --current"
	// 					>{item}</span>;
	// 	}

	// 	return <a href="!#" className="page-navigation__item --page-number" onClick={props.handleLoadPage}>{item}</a>
	// });

	return (
		<div id="page-navigation">
			<nav className="page-navigation">
				<div className="page-navigation__wrapper">
					{ props.currentPage > 1 &&
						<a
						href="!#"
						className="page-navigation__item --page-previous"
						><FontAwesomeIcon icon={faChevronLeft} /></a>
					}

					{pages}

					{ props.currentPage < props.totalOfPage  &&
						<a
							href="!#"
							className="page-navigation__item --page-next"
							><FontAwesomeIcon icon={faChevronRight} /></a>
					}
				</div>
			</nav>
		</div>
	);
}

export default PagePagination
